import React, { useState } from "react";
import "./ForgotPassword.css"; // Create this CSS file for styling
import { useNavigate } from "react-router-dom";

const ForgotPassword = () => {
  const [email, setEmail] = useState(""); // State for email input
  const navigate = useNavigate(); // Hook for navigation

  const handleResetPassword = (e) => {
    e.preventDefault(); // Prevent default form submission
    // Add your reset password logic here (e.g., API call to send reset email)
    console.log(`Password reset link sent to: ${email}`);
    alert("If this email is registered, you will receive a password reset link shortly.");
    setEmail(""); // Clear the email field after submission
  };

  return (
    <div className="forgot-password-container">
      {/* Left Section with Illustration */}
      <div className="forgot-password-left">
        <img
          src="/images/password-illustration.png"
          alt="Forgot Password Illustration"
          className="illustration"
        />
      </div>

      {/* Right Section with Form */}
      <div className="forgot-password-right">
        <h2>Forgot your password?</h2>
        <p>
          Please enter the email address associated with your account, and we
          will email you a link to reset your password.
        </p>
        <form className="forgot-password-form" onSubmit={handleResetPassword}>
          {/* Email Input Field */}
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Email"
            required
            className="email-input"
          />

          {/* Submit Button */}
          <button type="submit" className="btn-reset">
            Reset Password
          </button>

          {/* Back Button */}
          <button
            type="button"
            className="btn-back"
            onClick={() => navigate("/login")} // Navigate to Login page
          >
            Back
          </button>
        </form>
      </div>
    </div>
  );
};

export default ForgotPassword;
