import React,{ useState} from 'react';
import { useNavigate } from 'react-router-dom';

export default function ForgotPassword() {
  const navigate = useNavigate(); // Hook for navigation

  const [formData, setFormData] = useState({ email: '', mobile: '' });
  const [errors, setErrors] = useState({ email: '', mobile: '' });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
    setErrors((prevErrors) => ({
      ...prevErrors,
      [name]: '', // Clear error as user types
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const newErrors = {};

    if (!formData.email) {
      newErrors.email = 'Please enter your email.';
    }
    if (!formData.mobile) {
      newErrors.mobile = 'Please enter your mobile number.';
    }

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }

    console.log('Form Data:', formData);
  };


  return (

    
    <div className="vh-100 d-flex justify-content-center align-items-center bg-light">
      
      <div className="d-flex shadow rounded w-50 bg-white">
        {/* Left Section */}
        <div className="bg-primary text-white p-4 d-flex flex-column justify-content-center" style={{ width: '50%' }}>
          <h1 className="mb-3">KisanKart (KK)</h1>
          <p>Reset your password to continue accessing your account.</p>
          <div className="mt-auto">
            <video
              autoPlay
              loop
              muted
              className="w-100"
              style={{ borderRadius: '8px' }}
            >
              <source src="./images/ForgotPassword.mp4" type="video/mp4" />
              Your browser does not support the video tag.
            </video>
          </div>
        </div>

        {/* Right Section */}
        <div className="p-4" style={{ width: '50%' }}>
          <h2 className="mb-3">Forgot Password</h2>
          <p className="text-muted" style={{ fontSize: '0.9rem' }}>
            Enter your registered email address and Mobile Number to reset your password.
          </p>
          <form>
            <div className="mb-3">
              <label htmlFor="email" className="form-label">
                Email Address
              </label>
              <input
                type="email"
                id="email"
                name='email'
                value={formData.email}
                onChange={handleChange}
                placeholder="Enter your email address"
                className="form-control"
              />
              {errors.email && <small classname = 'text-danger'>{error}</small>}

              <label htmlFor="mobile" className="form-label mt-3">
                Mobile Number
              </label>
                  
               
              <input
                type="tel"
                id="mobile"
                placeholder="Mobile Number"
                className="form-control"
              />
            </div>

            <button type="submit" className="btn btn-warning w-100 mb-3">
              Reset Password
            </button>
            <button
              type="button"
              className="btn btn-light w-100"
              onClick={() => navigate('/login')} // Navigate back to login
            >
              Back to Login
            </button>
          </form>
          
        </div>
        
      </div>
      

    </div>
    
  );
}
